import { useEffect, useRef } from "react";

import { ContentBox } from "../styles";
import { ChildWrapper, StyledModal, StyledModalBackground, Title } from "./styles";

export const Modal = ({ title, setModalClose, children }) => {
  const modalRef = useRef(null);

  useEffect(() => {
    document.body.style.overflow = 'hidden';

    const modalElm = modalRef.current;
    const handleBackgroundClick = ({ target }) => {
      if (target !== modalElm) {
        setModalClose();
      }
    };

    if (modalElm) {
      modalElm.addEventListener('mouseclick', handleBackgroundClick);
    }

    return () => {
      document.body.style.overflow = 'auto';
      modalElm.removeEventListener('mouseclick', handleBackgroundClick);
    };
  }, []);

  return (
    <StyledModalBackground ref={modalRef}>
      <StyledModal>
        <ContentBox>
          {title &&
            <Title>
              {title}
            </Title>
          }
          {children &&
            <ChildWrapper>
              {children}
            </ChildWrapper>}
        </ContentBox>
      </StyledModal>
    </StyledModalBackground>);
}
