import styled from "styled-components";

export const Wrapper = styled.div`
  justify-content: space-evenly;
  margin: 1rem;

  .column {
    flex-direction: column;
  }
`;

export const Input = styled.input`
  text-align: center;
  border-radius: 0.5rem;
`;
