import styled from "styled-components";

const StyledButton = styled.button`
  display: inline-block;
  padding: 12px 24px;
  font-size: 18px;
  font-weight: bold;
  text-align: center;
  text-decoration: none;
  color: #fff; /* White text */
  cursor: pointer;

  background-color: #ff6f61; /* Bright, playful color */
  border: 3px dashed #333; /* Dashed border for a cut-out look */
  border-radius: 10px; /* Slightly rounded corners */
  box-shadow: 4px 4px 8px rgba(0, 0, 0, 0.3); /* Shadow for depth */
  transform: rotate(-3deg); /* Slight rotation for a hand-placed feel */
  transition: all 0.2s ease-in-out; /* Smooth transitions for hover effects */

&:hover {
  background-color: #ff8c61; /* Lighter shade on hover */
  transform: rotate(0deg) scale(1.05); /* Straighten and slightly enlarge on hover */
  box-shadow: 6px 6px 12px rgba(0, 0, 0, 0.4); /* Enhanced shadow on hover */
}

&:active {
  transform: translateY(2px) scale(0.98); /* Simulate a press effect */
  box-shadow: 2px 2px 4px rgba(0, 0, 0, 0.2); /* Reduced shadow when pressed */
}
`;

export const Button = () => <button>test</button>
