import { useEffect } from "react";

import { StyledModal } from "./styles";

export const Modal = ({ children }) => {
  useEffect(() => {
    document.body.style.overflow = 'hidden';

    return () => {
      document.body.style.overflow = 'auto';
    };
  }, []);

  return (<StyledModal>
    <div>
      {children}
    </div>
  </StyledModal>);
}
